import React, {useState, useEffect} from "react";
import './Recenzije.css';
import reviewData from "./ReviewsData";


const Recenzije = () => {
  const [people] = useState(reviewData);
  const [index, setIndex] = useState(0);


  useEffect(() => {
    const lastIndex = people.length - 1;
    if(index < 0){
      setIndex(lastIndex);
    } 
    else if(index > lastIndex){
      setIndex(0);
    }
  }, [index, people])

  useEffect(() => {
    let slider = setInterval(() => {
      setIndex(index + 1)
    }, 5000);
    return() => {
      clearInterval(slider)
    }
  }, [index])

  return(
    <section className="section">
       <div className="title">
          <h2>Recenzije</h2>
       </div>
       <div className="section-center">
         {people.map((item, indexPeople) => {
            const {id, name, review, background} = item; 
            let position = 'nextSlide';
            if(indexPeople === index){
              position = 'activeSlide';
            }
            if(indexPeople === index - 1 || (index === 0 && indexPeople === people.length - 1)){
              position = 'lastSlide';
            }
            return (
              <div style={{background}} className="review-container">
              <article className={position} key={id}>
                 <h1 className="name">{name}</h1>
                 <p className="review">{review}</p>
              </article>
              </div>
            )
         })}
       </div>
    </section>
  )
}

export default Recenzije;
