import React, {useRef} from "react";
import emailjs from '@emailjs/browser';
import './Contact.css';

const Contact = () => {
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();
    
        emailjs.sendForm('service_usueuep', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      };

    return(
        <div className="contact-container">
            <div  className="map">
              <h1>Where To Find Us ? <span><i class="fa-solid fa-hand-pointer fa-rotate-180"></i></span></h1>
              <iframe src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d63027.1066088316!2d18.315420487826582!3d44.90409659351073!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1shr!2sba!4v1691186799692!5m2!1shr!2sba" style={{width: "100%", height: "450px", border: '0'}}  allowFullScreen="" loading="lazy" referrerPolicy="no-referrer-when-downgrade"></iframe>
            </div>

            <div className="contact-info">
                <div className="contact-info-left">
                    <h1>Kontaktiraj nas</h1>
                    <p>Hećić Stolarija d.o.o. firma za prodaju i proizvodnju namještaja</p>
                    <p>Adresa: 25. Novembra br. 20, Gradačac</p>
                    <p>stolarija.hecic@hotmail.com
                        <br></br>
                       tel: +387 61 886 715</p>
                    <p className="contact-icons"><a href="https://www.facebook.com/stolarijahecic"><i class="fa-brands fa-facebook fa-fade"></i></a>  <a href="https://www.facebook.com/stolarijahecic"><i class="fa-brands fa-instagram fa-fade"></i></a></p>   
                </div>

                <div className="contact-info-right">
                   <form ref={form} onSubmit={sendEmail} className="contact-form">
                      <div className="name-number">
                        <label for='ime'></label>
                        <input className="input-name" id="ime" type="text" placeholder="Vaše Ime"></input>

                        <label for='broj'></label>
                        <input className="input-number" id="broj" type="number" placeholder="Vaš Broj"></input>
                      </div>
                      <div className="email-message">
                        <label for='email'></label>
                        <input className="input-email" id="email" type="email" placeholder="Vaš Email"></input>

                        <label for='message'></label>
                        <textarea className="input-message" id="message" placeholder="Vaša Poruka"></textarea>
                      </div>
                      <button type="submit" className="contact-form-button">Pošalji</button>
                   </form>
                </div>
            </div>
        </div>
    )
}

export default Contact;
